input.code:

using YS.Knife;
namespace ConsoleApp2
{
   [AutoConstructor()]
    partial class Class1<T>
    {
        private int value;

    }
    partial class Class2
    {
        [AutoConstructor()]
        partial class Class3<T>
        {
            private int value;

        }

    }
}

output.code:

namespace ConsoleApp2
{
    partial class Class1<T>
    {
        public Class1(int value)
        {
            this.value = value;
        }
    }
}


namespace ConsoleApp2
{
    partial class Class2
    {
        partial class Class3<T>
        {
            public Class3(int value)
            {
                this.value = value;
            }
        }
    }
}
