output.code:

namespace ConsoleApp2
{
    partial class Class2
    {
        public Class2(int args)
        {
            this.__ = args;
        }
    }
}


namespace ConsoleApp2
{
    partial class Class1
    {
        public Class1(int args, string value)
            : base(args: args)
        {
            this.value = value;
        }
    }
}
