input.code:

using YS.Knife;
namespace ConsoleApp2
{
    [AutoConstructor(NullCheck=true)]
    partial class Class1
    {
        private int value;

        private string str;

    }
}

output.code:

namespace ConsoleApp2
{
    partial class Class1
    {
        public Class1(int value, string str)
        {
            this.value = value;
            this.str = str ?? throw new System.ArgumentNullException(nameof(str));
        }
    }
}
