using YS.Knife;
namespace ConsoleApp2
{
    partial class Class1
    {
        [AutoNotify]
        private int _age;

    }
}


using YS.Knife;
namespace ConsoleApp2
{
    partial class Class2: Class1
    {
        [AutoNotify]
        private int _val;

    }
}

output.code:

using System.ComponentModel;
namespace ConsoleApp2
{
    partial class Class1 : INotifyPropertyChanged
    {
        public event PropertyChangedEventHandler PropertyChanged;

        public int Age 
        {
            get 
            {
                return this._age;
            }
            set
            {
                if(this._age != value)
                {
                    this._age = value;
                    this.PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(nameof(Age)));
                }
            }
        }

    }
}


using System.ComponentModel;
namespace ConsoleApp2
{
    partial class Class2
    {

        public int Val 
        {
            get 
            {
                return this._val;
            }
            set
            {
                if(this._val != value)
                {
                    this._val = value;
                    this.PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(nameof(Val)));
                }
            }
        }

    }
}
