input.code:

using YS.Knife;
namespace ConsoleApp2
{
    [SingletonPattern]
    partial class Class1
    {

    }
}

output.code:

using System;
namespace ConsoleApp2
{
    partial class Class1
    {
        private static readonly Lazy<Class1> LazyInstance = new Lazy<Class1>(() => new Class1(), true);

        private Class1() { }

        public static Class1 Instance => LazyInstance.Value;
    }
}
